SELECT * FROM public."individuals"
WHERE "first_name" ilike '%{{Individuals_Table.searchText || ""}}%'
ORDER BY "{{Individuals_Table.sortOrder.column || 'first_name'}}" {{Individuals_Table.sortOrder.order || 'ASC'}}
LIMIT {{Individuals_Table.pageSize}}
OFFSET {{(Individuals_Table.pageNo - 1) * Individuals_Table.pageSize}};
